print("\033c")
words = listString.split(' ')
numStrikes = 0
selectedWord = words[random.randrange(0,len(words))]
wrongGuesses = list('')
#print(selectedWord)

guessWord = ''

for i in range(0, len(selectedWord)):
    guessWord+='_'

def showWord():
				for i in range(len(guessWord)):
    					print(guessWord[i], end='')
				print("\nYou currently have %d strikes" %numStrikes)
				print("The following letters are not correct: %s" %wrongGuesses)


def putLetter(guess):
    s = list(guessWord)
    for i in range(0, len(s)):
        if selectedWord[i] == guess:
            s[i] = guess
    return ''.join(s)

showWord()

while guessWord != selectedWord and numStrikes < 5:	
				guess = input("\nGuess a letter: ")

				if(selectedWord.__contains__(guess)):
								print("Correct")
								guessWord = putLetter(guess)
								print("\033c")
								showWord()
				else:
								print("Incorrect")
								numStrikes+=1
								wrongGuesses += '%s'%guess
								print("\033c")
								showWord()
if(guessWord == selectedWord):
			print("You Win!")
else:
			print("You Lose, the word was %s" %selectedWord)

exit = False
while not exit:
    input()
    exit = True
